
public class 문제_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 3; 
	     a = a + 1; 
	     System.out.println(a);  //4
	     a += 1; 
	     System.out.println(a); //5
	     a -= 1; 
	     System.out.println(a); //4
	     a *= 2;
	     System.out.println(a); //8
	     a /= 2; 
	     System.out.println(a); //4
	     a %= 2; 
	     System.out.println(a); //0

	}

}
